mapper.select:

	select n_code, n_n_title, n_n_content, n_regdate, n_hit, n_img
	from (
		select /*+INDEX_DESC (notice pk_notice) */
			rownum rn, n_code, n_n_title, n_n_content, n_regdate, n_hit, n_img
		from notice
		where 
		
		
		
		rownum <= #{pageNum} * #{amount}
	)
	where rn > (#{pageNum}-1) * #{amount}
